import { useState, useEffect } from 'react';
import './FilterSidebar.css';

const FilterSidebar = ({ onFilterChange }) => {
  const [maxPrice, setMaxPrice] = useState(0); // Теперь это состояние
  const [priceRange, setPriceRange] = useState(0);
  const [material, setMaterial] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [availability, setAvailability] = useState('');
  const [brand, setBrand] = useState('');
  const [market, setMarket] = useState('');
  const [filterOptions, setFilterOptions] = useState({
    material: [],
    color: [],
    size: [],
    availability: [],
    brand: [],
    market: []
  });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('/api/products');
        if (!response.ok) throw new Error('Ошибка загрузки данных');
        const products = await response.json();
        
        if (!Array.isArray(products)) return;

        // Получаем все цены и находим максимум
        const validPrices = products
          .map(p => typeof p.price === 'number' ? p.price : 0)
          .filter(price => !isNaN(price));
        
        const calculatedMaxPrice = validPrices.length > 0 
          ? Math.max(...validPrices) 
          : 0;

        setMaxPrice(calculatedMaxPrice);
        setPriceRange(calculatedMaxPrice); // Устанавливаем ползунок на максимум

        // Заполняем остальные фильтры
        setFilterOptions({
          material: [...new Set(products.map(p => p.material).filter(Boolean))],
          color: [...new Set(products.map(p => p.color).filter(Boolean))],
          size: [...new Set(products.map(p => p.dimensions).filter(Boolean))],
          availability: ['В наличии', 'Нет в наличии'],
          brand: [...new Set(products.map(p => p.brand).filter(Boolean))],
          market: [...new Set(products.map(p => p.shop_id).filter(Boolean))]
        });

      } catch (error) {
        console.error('Ошибка при загрузке продуктов:', error);
      }
    };

    fetchProducts();
  }, []);
  
  const applyFilters = () => {
    const filters = {
      price: priceRange === maxPrice ? null : priceRange,
      material: material || null,
      color: color || null,
      dimensions: size || null,
      availability: availability === 'В наличии' ? true : availability === 'Нет в наличии' ? false : null,
      brand: brand || null,
      shop_id: market || null
    };
    
    console.log('Applying filters:', filters); // Для отладки
    onFilterChange(filters);
  };

  // Исправленный handlePriceChange
  const handlePriceChange = (event) => {
    const newPrice = parseInt(event.target.value);
    if (!isNaN(newPrice)) {
      setPriceRange(newPrice);
      // Применяем фильтр сразу при изменении ползунка
      onFilterChange({
        price: newPrice,
        material,
        color,
        dimensions: size,
        availability: availability === 'В наличии' ? true : availability === 'Нет в наличии' ? false : null,
        brand,
        shop_id: market
      });
    }
  };

  const handleMaterialChange = (e) => setMaterial(e.target.value);
  const handleColorChange = (e) => setColor(e.target.value);
  const handleSizeChange = (e) => setSize(e.target.value);
  const handleAvailabilityChange = (e) => setAvailability(e.target.value);
  const handleBrandChange = (e) => setBrand(e.target.value);
  const handleMarketChange = (e) => setMarket(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleResetFilters = () => {
    setPriceRange(maxPrice);
    setMaterial('');
    setColor('');
    setSize('');
    setAvailability('');
    setBrand('');
    setMarket('');
    applyFilters(); // Применяем сброшенные фильтры
  };

  return (
    <div className="filter-sidebar">
      <h2>Фильтры</h2>
      <form onSubmit={handleSubmit}>
        <div classname="price-range-container">
          <label htmlFor="price">Цена</label>
          <input
            type="range"
            id="price"
            min="0"
            max={maxPrice}
            step="1000"
            value={priceRange}
            onChange={handlePriceChange}
            className="price-input"
          />
          <span className="price-value"> до {priceRange.toLocaleString()} ₽</span>
        </div>
        <div>
          <label htmlFor="material">Материал</label>
          <select id="material" value={material} onChange={handleMaterialChange}>
            <option value="">Любой</option>
            {filterOptions.material.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <div>
        <label htmlFor="color">Цвет</label>
          <select id="color" value={color} onChange={handleColorChange}>
            <option value="">Любой</option>
            {filterOptions.color.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <div>
        <label htmlFor="size">Размер</label>
          <select id="size" value={size} onChange={handleSizeChange}>
            <option value="">Любой</option>
            {filterOptions.size.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <div>
        <label htmlFor="availability">Наличие</label>
          <select id="availability" value={availability} onChange={handleAvailabilityChange}>
            <option value="">Все</option>
            {filterOptions.availability.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <div>
        <label htmlFor="brand">Бренд</label>
          <select id="brand" value={brand} onChange={handleBrandChange}>
            <option value="">Любой</option>
            {filterOptions.brand.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <div>
        <label htmlFor="market">Магазин</label>
          <select id="market" value={market} onChange={handleMarketChange}>
            <option value="">Любой</option>
            {filterOptions.market.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <button type="submit">Применить</button>
      </form>
    </div>
  );
};

export default FilterSidebar;
