import React, { useState, useEffect, useCallback, useRef } from 'react';
import './FilterSidebar.css';

const FilterSidebar = ({ onFilterChange }) => {
  const maxPrice = 10000000; // Maximum price value
  const [priceRange, setPriceRange] = useState(maxPrice);
  const [material, setMaterial] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [availability, setAvailability] = useState('');
  const [brand, setBrand] = useState('');
  const [market, setMarket] = useState('');
  const [filterOptions, setFilterOptions] = useState({
    material: [],
    color: [],
    size: [],
    availability: [],
    brand: [],
    market: []
  }); // Объект: { material: ['дерево', 'металл'], color: [...] }

  // Refs для хранения значений фильтров и таймеров
  const filtersRef = useRef({});
  const priceTimerRef = useRef(null);
  const filtersTimerRef = useRef(null);

  useEffect(() => {
    fetch('/api/products')
      .then(res => res.json())
      .then(products => {
        // Extract unique values for each filter category
        const materials = [...new Set(products.map(p => p.material).filter(Boolean))];
        const colors = [...new Set(products.map(p => p.color).filter(Boolean))];
        const sizes = [...new Set(products.map(p => p.dimensions).filter(Boolean))];
        const availabilities = ['В наличии', 'Нет в наличии']; // Assuming this is calculated from stock_quantity
        const brands = [...new Set(products.map(p => p.brand).filter(Boolean))];
        const markets = [...new Set(products.map(p => p.shop_id).filter(Boolean))];

        setFilterOptions({
          material: materials,
          color: colors,
          size: sizes,
          availability: ['В наличии', 'Нет в наличии'],
          brand: brands,
          market: markets
        });
      });
    // При монтировании компонента устанавливаем максимальное значение цены
    // setPriceRange(maxPrice);
    return () => {
      clearTimeout(priceTimerRef.current);
      clearTimeout(filtersTimerRef.current);
    };
  }, []);
  
  const sendFilters = useCallback((immediate = false) => {
    const filters = {
      price: priceRange,
      material: material || null,
      color: color || null,
      dimensions: size || null,
      availability: availability === 'В наличии' ? true : availability === 'Нет в наличии' ? false : null,
      brand: brand || null,
      shop_id: market || null
    };

    // Проверяем, изменились ли фильтры
    if (JSON.stringify(filtersRef.current) === JSON.stringify(filters)) {
      return;
    }

    filtersRef.current = filters;

    if (immediate) {
      onFilterChange(filters);
    } else {
      clearTimeout(filtersTimerRef.current);
      filtersTimerRef.current = setTimeout(() => {
        onFilterChange(filters);
      }, 300);
    }
  }, [priceRange, material, color, size, availability, brand, market, onFilterChange]);

  const handlePriceChange = (event) => {
    const newPrice = parseInt(event.target.value, 10);
    setPriceRange(newPrice);
    clearTimeout(priceTimerRef.current);
    priceTimerRef.current = setTimeout(() => {
      sendFilters(true); // Применяем фильтры сразу после остановки ползунка
    }, 150);
  };

  const handleMaterialChange = (e) => setMaterial(e.target.value);
  const handleColorChange = (e) => setColor(e.target.value);
  const handleSizeChange = (e) => setSize(e.target.value);
  const handleAvailabilityChange = (e) => setAvailability(e.target.value);
  const handleBrandChange = (e) => setBrand(e.target.value);
  const handleMarketChange = (e) => setMarket(e.target.value);

  return (
    <div className="filter-sidebar">
      <h2>Фильтры</h2>
      <div>       
        <div className="price-range-container">
          <label htmlFor="price">Цена</label>
          <input
            type="range"
            id="price"
            min="0"
            max={maxPrice}
            value={priceRange}
            onChange={handlePriceChange}
            step="1000"
            className="price-input"
          />
          <span className="price-value"> до {priceRange.toLocaleString()} ₽</span>
        </div>
        <FilterSelect 
          label="Материал"
          id="material"
          value={material}
          options={filterOptions.material}
          onChange={handleMaterialChange}
        />

        <FilterSelect 
          label="Цвет"
          id="color"
          value={color}
          options={filterOptions.color}
          onChange={handleColorChange}
        />

        <FilterSelect 
          label="Размер"
          id="size"
          value={size}
          options={filterOptions.size}
          onChange={handleSizeChange}
        />

        <FilterSelect 
          label="Наличие"
          id="availability"
          value={availability}
          options={filterOptions.availability}
          onChange={handleAvailabilityChange}
        />

        <FilterSelect 
          label="Бренд"
          id="brand"
          value={brand}
          options={filterOptions.brand}
          onChange={handleBrandChange}
        />

        <FilterSelect 
          label="Магазин"
          id="market"
          value={market}
          options={filterOptions.market}
          onChange={handleMarketChange}
        />
      </div>
    </div>
  );
};

// Вынесенный компонент для селектов
const FilterSelect = ({ label, id, value, options, onChange }) => (
  <div className="filter-group">
    <label htmlFor={id}>{label}</label>
    <select id={id} value={value} onChange={onChange}>
      <option value="">{label === 'Наличие' ? 'Все' : 'Любой'}</option>
      {options.map((val) => (
        <option key={val} value={val}>{val}</option>
      ))}
    </select>
  </div>
);

export default FilterSidebar;
