// CategoryPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import ProductCard from './ProductCard'; // Import ProductCard
import ProductList from './ProductList';

const CategoryPage = ({ products, searchTerm }) => {
    const { categoryName } = useParams();

    // const categoryFilteredProducts = products.filter(product => {
    //     const matchesCategory = decodeURIComponent(categoryName) === product.category;
    //     const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    
    //     return matchesCategory && matchesSearch;
    // });

    return (
        <div>
            <h2>Категория: {categoryName}</h2>
            {products.length > 0 ? (
                <ProductList products={products} />
            ) : (
                <p>Нет товаров в данной категории.</p>
            )}
    </div>
    );
}

export default CategoryPage;
