import { useState, useEffect } from 'react';
import './FilterSidebar.css';

const FilterSidebar = ({ onFilterChange, initialPrice = 10000000 }) => {
  const maxPrice = 10000000;
  const [priceRange, setPriceRange] = useState(initialPrice);
  const [material, setMaterial] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [availability, setAvailability] = useState('');
  const [brand, setBrand] = useState('');
  const [market, setMarket] = useState('');
  const [filterOptions, setFilterOptions] = useState({
    material: [],
    color: [],
    size: [],
    availability: [],
    brand: [],
    market: []
  });

  useEffect(() => {
    fetch('/api/products')
      .then(res => res.json())
      .then(products => {
        // Находим максимальную цену среди товаров
        const maxProductPrice = Math.max(...products.map(p => p.price || 0), maxPrice);
        // Extract unique values for each filter category
        const materials = [...new Set(products.map(p => p.material).filter(Boolean))];
        const colors = [...new Set(products.map(p => p.color).filter(Boolean))];
        const sizes = [...new Set(products.map(p => p.dimensions).filter(Boolean))];
        const availabilities = ['В наличии', 'Нет в наличии']; // Assuming this is calculated from stock_quantity
        const brands = [...new Set(products.map(p => p.brand).filter(Boolean))];
        const markets = [...new Set(products.map(p => p.shop_id).filter(Boolean))];

        setFilterOptions({
          material: materials,
          color: colors,
          size: sizes,
          availability: availabilities,
          brand: brands,
          market: markets
        });
        // Устанавливаем максимальную цену из товаров или дефолтную
        setPriceRange(Math.min(maxProductPrice, initialPrice));
      });
  }, []);
  
  const applyFilters = () => {
  onFilterChange({
    price: priceRange === maxPrice ? null : priceRange,
    material: material || null,
    color: color || null,
    dimensions: size || null,
    availability: availability === 'В наличии' ? true : availability === 'Нет в наличии' ? false : null,
    brand: brand || null,
    shop_id: market || null
  });
};

  const handlePriceChange = (event) => {
    const newPrice = parseInt(event.target.value, 10);
    setPriceRange(newPrice);
  };

  const handleMaterialChange = (e) => setMaterial(e.target.value);
  const handleColorChange = (e) => setColor(e.target.value);
  const handleSizeChange = (e) => setSize(e.target.value);
  const handleAvailabilityChange = (e) => setAvailability(e.target.value);
  const handleBrandChange = (e) => setBrand(e.target.value);
  const handleMarketChange = (e) => setMarket(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    applyFilters();
  };

  const handleResetFilters = () => {
    setPriceRange(maxPrice);
    setMaterial('');
    setColor('');
    setSize('');
    setAvailability('');
    setBrand('');
    setMarket('');
    applyFilters(); // Применяем сброшенные фильтры
  };

  // const handleSubmit = (event) => { //USELESSSS
  //   event.preventDefault();
    
  //   // Convert filter values to match database structure
  //   const filters = {
  //     price: priceRange,
  //     material: material || null,
  //     color: color || null,
  //     dimensions: size || null,
  //     availability: availability === 'В наличии' ? true : availability === 'Нет в наличии' ? false : null,
  //     brand: brand || null,
  //     shop_id: market || null
  //   };

  //   onFilterChange(filters);
  // };

  return (
    <div className="filter-sidebar">
      <h2>Фильтры</h2>
      <form onSubmit={handleSubmit}>
        <div className="price-range-container">
          <label htmlFor="price">Цена</label>
          <input
            type="range"
            id="price"
            min="0"
            max={maxPrice}
            step="1000"
            value={priceRange}
            onChange={handlePriceChange}
            className="price-input"
          />
          <span className="price-value"> до {maxPrice.toLocaleString()} ₽</span>
        </div>
        <div className="filter-item">
          <label>Материал</label>
          <select 
            value={material} 
            onChange={handleFilterChange(setMaterial)}
          >
            <option value="">Все</option>
            {filterOptions.material.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Цвет</label>
          <select 
            value={color} 
            onChange={handleFilterChange(setColor)}
          >
            <option value="">Все</option>
            {filterOptions.color.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Размер</label>
          <select 
            value={size} 
            onChange={handleFilterChange(setSize)}
          >
            <option value="">Все</option>
            {filterOptions.size.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Наличие</label>
          <select 
            value={availability} 
            onChange={handleFilterChange(setAvailability)}
          >
            <option value="">Все</option>
            {filterOptions.availability.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Бренд</label>
          <select 
            value={brand} 
            onChange={handleFilterChange(setBrand)}
          >
            <option value="">Все</option>
            {filterOptions.brand.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Магазин</label>
          <select 
            value={market} 
            onChange={handleFilterChange(setMarket)}
          >
            <option value="">Все</option>
            {filterOptions.market.map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>
        <button type="submit">Применить</button>
      </form>
    </div>
  );
};

export default FilterSidebar;
