import React from 'react';
import defaultImage from './default-product-image.jpg'; // Запасное изображение

const ProductCard = ({ product }) => {
  // Проверяем наличие product и его свойств
  if (!product) {
    return <div className="product-card error">Ошибка: данные товара не загружены</div>;
  }
  // Construct image path
  const getImageSource = () => {     
      // 1. Проверяем локальные пути (image_paths)
      if (product.image_paths && product.image_paths.length > 0) {
        // Преобразуем строку в массив, если нужно
        const pathsArray = Array.isArray(product.image_paths) 
            ? product.image_paths 
            : product.image_paths.split(',').map(path => path.trim());
        
        // Берем первое изображение
        const firstPath = pathsArray[0];
        
        // Удаляем 'full/' если он есть в начале
        const cleanPath = firstPath.startsWith('full/') 
            ? firstPath.substring(5) 
            : firstPath;
            
        return `/images/full/${cleanPath}`;
    }
      
      // 2. Если нет локальных путей, пробуем оригинальные URL
      if (product.images && product.images.length > 0) {
          return product.images[0];
      }
      
      // 3. Если ничего нет, возвращаем дефолтное изображение
      return defaultImage;
  };

  // // Проверяем данные в консоли для отладки
  // console.log('Product data:', {
  //   id: product.id,
  //   name: product.name,
  //   images: product.images,
  //   image_paths: product.image_paths,
  //   resolvedImage: getImageSource()
  // });

  // Format price
  const formattedPrice = product.price ? 
  `${product.price.toLocaleString('ru-RU')} руб.` : 
  'Цена не указана';

  const handleShopClick = (url) => {
    // Проверяем, что url существует
    if (!url) {
      console.error('URL is undefined or empty');
      return;
    }
    
    // Логируем оригинальный URL для отладки
    console.log('Original URL:', url);
    
    // Добавляем протокол если отсутствует
    let processedUrl = url;
    if (!url.startsWith('http')) {
      processedUrl = 'https://' + url;
    }
    
    try {
      new URL(processedUrl); // Валидация URL
      window.open(processedUrl, '_blank', 'noopener,noreferrer');
    } catch (e) {
      console.error('Invalid URL:', processedUrl, e);
    }
  };

  return (
    <div className="product-card">
      <div className="product-image-container">
          <img
              src={getImageSource()} 
              alt={product.name || 'Изображение товара'}
              className="product-image"
              onError={(e) => {
                console.error('Image load error:', {
                  attemptedUrl: e.target.src,
                  productId: product.id,
                  error: e.nativeEvent
                });
                  e.target.src = defaultImage;
              }}
          />
      </div>
      <div className="product-info">
        <h3>{product.name}</h3>
        {product.description && <p className="description">{product.description}</p>}
        <p className="price">{formattedPrice}</p>
        {product.shop_name && <p className="store">Магазин: {product.shop_name}</p>}
        <button 
          onClick={() => product.original_url && handleShopClick(product.original_url)}
          className="shop-button"
          disabled={!product.original_url}
        >
          {product.original_url ? 'Перейти в магазин' : 'Ссылка недоступна'}
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
