import React from 'react';
import defaultImage from './default-product-image.jpg'; // Запасное изображение

const ProductCard = ({ product }) => {
  // Construct image path
  const getImageSource = () => {
      // 1. Проверяем локальные пути (image_paths)
      if (product.image_paths && product.image_paths.length > 0) {
          // Берем первое изображение из массива
          const localPath = product.image_paths[0];
          // Удаляем 'full/' из пути, если он есть
          const cleanPath = localPath.replace('full/', '');
          return `/images/full/${cleanPath}`;
      }
      
      // 2. Если нет локальных путей, пробуем оригинальные URL
      if (product.images && product.images.length > 0) {
          return product.images[0];
      }
      
      // 3. Если ничего нет, возвращаем дефолтное изображение
      return defaultImage;
  };
  // Format price
  const formattedPrice = product.price ? 
  `${product.price.toLocaleString('ru-RU')} руб.` : 
  'Цена не указана';

  return (
    <div className="product-card">
      <div className="product-image-container">
      <img
        src={getImageSource()} 
        alt={product.name || 'Изображение товара'}
        onError={(e) => {
          e.target.src = defaultImage;
      }}
      />
      </div>
      <div className="product-info">
        <h3>{product.name}</h3>
        {product.description && <p className="description">{product.description}</p>}
        <p className="price">{formattedPrice}</p>
        {product.shop_name && <p className="store">Магазин: {product.shop_name}</p>}
        <a 
          href={product.link} 
          target="_blank" 
          rel="noopener noreferrer"
          className="shop-link"
        >
          Перейти в магазин
        </a>
      </div>
    </div>
  );
};

export default ProductCard;
