import React from 'react';
import defaultImage from './default-product-image.jpg'; // Запасное изображение

const ProductCard = ({ product }) => {
    // Construct image path
    const getImageSource = () => {
      // First try to find an image in the public/images folder
      if (product.image_urls && product.image_urls.length > 0) {
        const imageName = product.image_urls[0].split('/').pop();
        return `/images/${imageName}`;
      }
      
      return defaultImage;
  };
  // Format price
  const formattedPrice = product.price ? 
  `${product.price.toLocaleString('ru-RU')} руб.` : 
  'Цена не указана';

  return (
    <div className="product-card">
      <div className="product-image-container">
      <img
        src={getImageSource()} 
        alt={product.name || 'Изображение товара'}
        onError={(e) => {
          e.target.src = defaultImage;
      }}
      />
      </div>
      <div className="product-info">
        <h3>{product.name}</h3>
        {product.description && <p className="description">{product.description}</p>}
        <p className="price">{formattedPrice}</p>
        <p className="store">Магазин: {storeName}</p>
        <a 
          href={product.link} 
          target="_blank" 
          rel="noopener noreferrer"
          className="shop-link"
        >
          Перейти в магазин
        </a>
      </div>
    </div>
  );
};

export default ProductCard;
